template.content:
import request from '@/router/axios'

export function page${ClassName}(query) {
  return request({
    url: '/${urlPrefix}/',
    method: 'get',
    params: query
  })
}

export function save${ClassName}(obj) {
  return request({
    url: '/${urlPrefix}/',
    method: 'post',
    data: obj
  })
}

export function find${ClassName}(id) {
  return request({
    url: '/${urlPrefix}/' + id,
    method: 'get'
  })
}

export function remove${ClassName}(id) {
  return request({
    url: '/${urlPrefix}/' + id,
    method: 'delete'
  })
}
